mapper.select:
SELECT DISTINCT(A.end_month), SUM(D.pwr_qty) AS "pwrQty", SUM(D.total_bill) AS "totalBill", SUM(C.max_power) AS "maxPower", () AS "savingTotalBill" FROM ems.cyber_kepco_cost A INNER JOIN ems.cyber_kepco_info B ON A.cust_no = B.cust_no AND B.end_month = A.end_month AND B.sel_cost = A.sel_cost LEFT JOIN ems.real_time_info C ON A.cust_no = C.cust_no AND mr_ym = A.end_month LEFT JOIN ems.cyber_kepco_cost_detail D ON A.cust_no = D.cust_no AND bill_ym = A.end_month AND D.sel_cost like '%'||A.sel_cost||'%' WHERE A.end_month between '201810' and #{tgtDate}
AND A.cust_no = null
AND A.cust_no in
#{item.ismartId}
GROUP BY A.end_month ORDER BY 1 desc

SELECT (ROW_NUMBER() OVER(ORDER BY "pwrQty" asc)) AS "rnum" , * FROM ( SELECT D.ismart_no AS "ismartId", CASE WHEN E.site_name IS NOT NULL THEN E.site_name ELSE F.user_id END AS "siteName", D.latt AS "latt", D.lngt AS "lngt", H.pwr_qty AS "pwrQty", CASE WHEN D.class_cnt is null THEN null ELSE ROUND(H.pwr_qty / D.class_cnt,2) END AS "pwrPerClass" FROM ems.ismart_bas D LEFT JOIN ems.total_info E ON E.cust_no = D.ismart_no LEFT JOIN ems.user_bas F ON F.ismart_id = D.ismart_no INNER JOIN ems.cyber_kepco_info G ON D.ismart_no = G.cust_no AND G.end_month = (SELECT max(end_month) from ems.cyber_kepco_info WHERE cust_no = G.cust_no) INNER JOIN ems.cyber_kepco_cost H ON D.ismart_no = H.cust_no AND H.sel_cost = G.sel_cost AND H.end_month = (SELECT MAX(end_month) FROM ems.cyber_kepco_cost WHERE cust_no = D.ismart_no) WHERE F.user_id NOT LIKE '%test%'
AND D.ismart_no = null
AND D.ismart_no in
#{item.ismartId}
) AS AA ORDER BY "pwrQty" asc
