mapper.select:
SELECT DISTINCT(A.end_month), SUM(D.pwr_qty) AS "pwrQty", SUM(D.total_bill) AS "totalBill", SUM(C.max_power) AS "maxPower" FROM ems.cyber_kepco_cost A INNER JOIN ems.cyber_kepco_info B ON A.cust_no = B.cust_no AND B.end_month = A.end_month AND B.sel_cost = A.sel_cost LEFT JOIN ems.real_time_info C ON A.cust_no = C.cust_no AND mr_ym = A.end_month LEFT JOIN ems.cyber_kepco_cost_detail D ON A.cust_no = D.cust_no AND bill_ym = A.end_month AND D.sel_cost like '%'||A.sel_cost||'%' WHERE A.end_month between '201810' and #{tgtDate}
AND A.cust_no = null
AND A.cust_no in
#{item.ismartId}
GROUP BY A.end_month ORDER BY 1 desc
